sys_script_include.script:
var Validate_emailaddress_OC_1 = Class.create();
Validate_emailaddress_OC_1.prototype = {
    initialize: function() {
    },

	emailFunc : function(emailStr)
	{
		var a='';
		emailStr = emailStr + '';
    // Compare emailStr against the allowed syntax as specified in the regular expression
    // If emailStr has allowed syntax, return true, else return false
    if(emailStr.match(/^(([^<>()\[\]\\.,;:\s@"]+(\.[^<>()\[\]\\.,;:\s@"]+)*)|(".+"))@((\[[0-9]{1,3}\.[0-9]{1,3}\.[0-9]{1,3}\.[0-9]{1,3}])|(([a-zA-Z\-0-9]+\.)+[a-zA-Z]{2,}))$/)){
      a = 'true';
    }
    else {
     a = 'false';
    }
		return a;
	},

	getEmailIDfunc : function()
	{
		var userRec = new GlideRecord('sys_user');
		userRec.get(this.getParameter('sysparm_email'))
		return userRec.email+'';
	},

    type: 'Validate_emailaddress_OC_1'
};
